mapper.select:
select distinct e.id, e.carrier_id, e.carrier_code, e.order_number, e.release_no, e.bill_of_lading, e.business_unit, e.consignee, e.container_number,
e.expired_dem, e.empty_container_depot, e.det_free_time, e.secure_code, e.release_date, e.vessel, e.vessel_no , e.voy_no, e.sztp,
e.release_status, e.create_source, e.remark, e.del_flg, e.create_by, e.file_create_time, e.create_time, e.update_by,
e.update_time, e.transaction_id, e.pol, e.pod, e.house_bill_id, e.consignee_by_tax_code, e.tax_code , s.status as status
from edo e
left join shipment_detail s on e.bill_of_lading = s.bl_no and e.container_number = s.container_no


    and e.carrier_code in

      #{carrier_code}



    and e.id in

      #{id}


  and e.id = #{id}
  and e.carrier_id = #{carrierId}
  and e.carrier_code = #{carrierCode}
  and e.order_number = #{orderNumber}
  and e.release_no = #{releaseNo}
  and e.bill_of_lading = #{billOfLading}
  and e.container_number = #{params.containerNumber}
  and e.business_unit = #{businessUnit}
  and e.empty_container_depot = #{emptyContainerDepot}
  and e.det_free_time = #{detFreeTime}
  and e.secure_code = #{secureCode}
  and e.release_date = #{releaseDate}
  and e.status = #{status}
  and e.vessel like concat(concat('%', #{vessel}), '%')
  and e.voy_no like concat(concat('%', #{voyNo}), '%')
  and e.release_status = #{releaseStatus}
  and e.create_source = #{createSource}
  and e.del_flg = #{delFlg}
  and (e.create_time = #{fromDate} OR e.create_time > #{fromDate})
  and (e.create_time = #{toDate} OR e.create_time < #{toDate})
  and (upper(e.consignee) like concat(concat('%', #{consignee}),'%')
  or upper(e.sztp) like concat(concat('%', #{sztp}), '%'))
  or upper(e.container_number) like concat(concat('%',#{containerNumber}), '%'))
  and e.house_bill_id = #{houseBillId}

ORDER BY e.create_time DESC




    and carrier_code in

      #{opr}



    and carrier_code in

      #{carrier_code}


  and carrier_id = #{carrierId}
  and bill_of_lading = #{params.blNo}
  and container_number = #{params.containerNo}
  and carrier_code = #{carrierCode}
  and order_number = #{orderNumber}
  and release_no = #{releaseNo}
  and business_unit = #{businessUnit}
  and empty_container_depot =
    #{emptyContainerDepot}

  and det_free_time = #{detFreeTime}
  and secure_code = #{secureCode}
  and upper(bill_of_lading) = #{params.billOfLading}
  and release_date = #{releaseDate}
  and status = #{status}
  and vessel like concat(concat('%', #{vessel}), '%')
  and voy_no like concat(concat('%', #{voyNo}), '%')
  and release_status = #{releaseStatus}
  and create_source = #{createSource}
  and del_flg = #{delFlg}
  and (create_time = #{fromDate} OR create_time > #{fromDate})
  and (create_time = #{toDate} OR create_time < #{toDate})
  and (upper(consignee) like concat(concat('%', #{consignee}),
    '%')

  or upper(bill_of_lading) like concat(concat('%', #{billOfLading}), '%')
  or upper(sztp) like concat(concat('%', #{sztp}), '%'))
  or upper(container_number) like concat(concat('%',
