FETCH NEXT 1 ROWS ONLY

SELECT bill_of_lading, carrier_code, vessel, voy_no,(SELECT create_time from edo e2 where
e1.bill_of_lading = e2.bill_of_lading ORDER BY create_time DESC FETCH NEXT 1 ROWS ONLY ) as create_time, (SELECT
COUNT(*) FROM edo e2 WHERE e1.bill_of_lading = e2.bill_of_lading and del_flg = 0) AS number_container from edo e1


    and carrier_code in

      #{carrier_code}


  and order_number = #{orderNumber}
  and release_no = #{releaseNo}
  and bill_of_lading like concat(concat('%',
    #{billOfLading}), '%')

  and carrier_code = #{carrierCode}
  and carrier_id = #{carrierId}
  and business_unit = #{businessUnit}
  and consignee = #{consignee}
  and upper(container_number) like upper(concat('%', concat(#{containerNumber}, '%')))
  and empty_container_depot =
    #{emptyContainerDepot}

  and det_free_time = #{detFreeTime}
  and secure_code = #{secureCode}
  and release_date = #{releaseDate}
  and vessel_no = #{vesselNo}
  and vessel like concat(concat('%', #{vessel}), '%')
  and voy_no = #{voyNo}
  and status = #{status}
  and release_status = #{releaseStatus}
  and create_source = #{createSource}
  and (create_time = #{fromDate} OR create_time > #{fromDate})
  and (create_time = #{toDate} OR create_time < #{toDate})

    and create_time <= to_date(#{params.toDate}, 'dd/MM/yyyy HH24:mi:ss')


    and create_time >= to_date(#{params.fromDate},'dd/MM/yyyy HH24:mi:ss')


GROUP BY bill_of_lading, carrier_code, vessel, voy_no
ORDER BY create_time DESC

select distinct vessel
from edo


    and carrier_code in

      #{carrier_code}


    and carrier_code = #{carrierCode}
  and vessel like concat(concat('%', #{vessel}), '%')


WHERE
  house_bill_id is null
  and bill_of_lading = #{billOfLading}
  and business_unit = #{carrierCode}
  and TO_CHAR(expired_dem, 'DD/MM/YYYY') = #{expiredDem}
  and order_number = #{orderNumber}
ORDER BY create_time DESC

select bill_of_lading from edo
where house_bill_id = #{id}
FETCH NEXT 1 ROWS ONLY

select e.id, e.carrier_id, e.carrier_code, e.order_number, b.house_bill_no as bill_of_lading, e.business_unit, e.consignee, e.container_number,
e.expired_dem, e.empty_container_depot, e.det_free_time, e.secure_code, e.release_date, e.vessel, e.vessel_no , e.voy_no, e.sztp,
e.status, e.release_status, e.create_source, e.remark, e.del_flg, e.create_by, e.file_create_time, e.create_time, e.update_by,
e.update_time, e.transaction_id, e.pol, e.pod, e.house_bill_id, e.consignee_by_tax_code, e.tax_code from edo e
right join edo_house_bill b on e.house_bill_id = b.id
WHERE
  b.house_bill_no = #{billOfLading}
  and e.business_unit = #{carrierCode}
  and TO_CHAR(e.expired_dem, 'DD/MM/YYYY') = #{expiredDem}
  and b.order_number = #{orderNumber}
ORDER BY e.create_time DESC

    SELECT 
			(SELECT COUNT(DISTINCT bill_of_lading) FROM edo where carrier_code in 

		            #{carrier_code}

        		) AS totalBl,
			(SELECT COUNT(container_number) FROM edo where carrier_code in 

		            #{carrier_code}

		        ) AS totalCont,
			(SELECT COUNT(DISTINCT bill_of_lading) FROM edo  where carrier_code in 

		            #{carrier_code}

		        AND status = 0) AS waitingBl,
			(SELECT COUNT(DISTINCT bill_of_lading) FROM edo where carrier_code in 

		            #{carrier_code}

		        AND status = 1) AS completedBl
            from edo
            FETCH NEXT 1 ROWS ONLY



 		and bill_of_lading = #{billOfLading}

        	and carrier_code in

          #{carrier_code}



		FETCH NEXT 1 ROWS ONLY

select * from edo
