mapper.select:


 and carrier_id = #{carrierId}
 and carrier_code = #{carrierCode}
 and order_number = #{orderNumber}
 and bill_of_lading = #{billOfLading}
 and business_unit = #{businessUnit}
 and empty_container_depot = #{emptyContainerDepot}
 and det_free_time = #{detFreeTime}
 and secure_code = #{secureCode}
 and release_date = #{releaseDate}
 and status = #{status}
 and release_status = #{releaseStatus}
 and create_source = #{createSource}
 and del_flg = #{delFlg}
and (create_time = #{fromDate} OR create_time > #{fromDate})
and (create_time = #{toDate} OR create_time < #{toDate})
 and (upper(consignee) like concat(concat('%', #{consignee}), '%')
 or upper(container_number) like concat(concat('%', #{containerNumber}), '%')
 or upper(vessel) like concat(concat('%', #{vessel}), '%')
 or upper(voy_no) like concat(concat('%', #{voyNo}), '%'))
 or upper(sztp) like concat(concat('%', #{sztp}), '%'))

ORDER BY create_time DESC



    and carrier_id = #{carrierId}
    and carrier_code = #{carrierCode}
    and order_number = #{orderNumber}
    and bill_of_lading = #{billOfLading}
    and business_unit = #{businessUnit}
    and empty_container_depot = #{emptyContainerDepot}
    and det_free_time = #{detFreeTime}
    and secure_code = #{secureCode}
    and release_date = #{releaseDate}
    and status = #{status}
    and release_status = #{releaseStatus}
    and create_source = #{createSource}
    and del_flg = #{delFlg}
   and (create_time = #{fromDate} OR create_time > #{fromDate})
   and (create_time = #{toDate} OR create_time < #{toDate})
    and consignee  = #{consignee}
   and container_number = #{containerNumber}
    and vessel = #{vessel}
     and vessel_no = #{vesselNo}
    and voy_no = #{voyNo}
    and voy_no = #{sztp}

FETCH NEXT 1 ROWS ONLY

mapper.update:
update edo
carrier_id = #{carrierId}, carrier_code = #{carrierCode}, order_number = #{orderNumber}, bill_of_lading = #{billOfLading}, business_unit = #{businessUnit}, consignee = #{consignee}, container_number = #{containerNumber}, expired_dem = #{expiredDem}, empty_container_depot = #{emptyContainerDepot}, det_free_time = #{detFreeTime}, secure_code = #{secureCode}, release_date = #{releaseDate}, vessel = #{vessel}, vessel_no = #{vesselNo}, voy_no = #{voyNo}, voy_no = #{sztp}, status = #{status}, release_status = #{releaseStatus}, create_source = #{createSource}, transaction_id = #{transactionId}, remark = #{remark}, del_flg = #{delFlg}, update_by = #{updateBy}, update_time = sysdate
where id = #{id}

mapper.select:
         SELECT bill_of_lading, carrier_code, business_unit, vessel, vessel_no, voy_no,(SELECT create_time from edo e2 where e1.bill_of_lading = e2.bill_of_lading ORDER BY create_time DESC FETCH NEXT 1 ROWS ONLY ) as create_time, (SELECT COUNT(*) FROM edo e2 WHERE e1.bill_of_lading = e2.bill_of_lading) AS number_container from edo e1

             and order_number = #{orderNumber}
             and bill_of_lading like concat(concat('%', #{billOfLading}), '%')
             and carrier_code = #{carrierCode}
             and business_unit = #{businessUnit}
             and consignee = #{consignee}
             and container_number = #{containerNumber}
             and empty_container_depot = #{emptyContainerDepot}
             and det_free_time = #{detFreeTime}
             and secure_code = #{secureCode}
             and release_date = #{releaseDate}
             and vessel_no = #{vesselNo}
              and vessel = #{vessel}
             and voy_no = #{voyNo}
             and status = #{status}
             and release_status = #{releaseStatus}
             and create_source = #{createSource}

				and date_format(expired_dem,'%y%m%d') <= date_format(#{params.toDate},'%y%m%d')


				and date_format(expired_dem,'%y%m%d') >= date_format(#{params.fromDate},'%y%m%d')


            GROUP BY bill_of_lading, carrier_code, business_unit,vessel , vessel_no, voy_no
