mapper.select:
	SELECT 
	    INV_NO AS INVOICE_NO,
	    NET_AMT AS EXCHANGE_FEE,
	    VAT_RATE AS VAT_RATE,
	    AMT AS VAT_AFTER_FEE,
	    CNTR_NO AS CONTAINER_NO,
	    SZTP2 AS SZTP
	FROM DNP_UAT.TB_INVOICE_UNIT
	WHERE ORDER_NO = (SELECT JOB_ODR_NO FROM DNP_UAT.TB_RESERVE 

					 CNTR_NO = #{containerNo}
					 AND BL_NO = #{blNo}
					 AND BOOKING_NO = #{bookingNo}
					 AND VSL_CD = #{vslNm}
					 AND CALL_SEQ = #{voyNo}
					 AND FE = #{fe}
					 AND PTNR_CODE = #{opeCode}			

        GROUP BY JOB_ODR_NO)

	SELECT 
	    INV_NO AS INVOICE_NO,
	    NET_AMT AS EXCHANGE_FEE,
	    VAT_RATE AS VAT_RATE,
	    AMT AS VAT_AFTER_FEE,
	    CNTR_NO AS CONTAINER_NO,
	    SZTP2 AS SZTP
	FROM DNP_UAT.TB_INVOICE_UNIT
	WHERE ORDER_NO = (SELECT JOB_ODR_NO2 FROM DNP_UAT.TB_INVENTORY 

					 CNTR_NO = #{containerNo}
					 AND BL_NO = #{blNo}
					 AND BOOKING_NO = #{bookingNo}
					 AND VSL_CD = #{vslNm}
					 AND CALL_SEQ = #{voyNo}
					 AND FE = #{fe}
					 AND PTNR_CODE = #{opeCode}			

        GROUP BY JOB_ODR_NO2)

	SELECT 
	    INV_NO AS INVOICE_NO,
	    NET_AMT AS EXCHANGE_FEE,
	    VAT_RATE AS VAT_RATE,
	    AMT AS VAT_AFTER_FEE,
	    CNTR_NO AS CONTAINER_NO,
	    SZTP2 AS SZTP
	FROM DNP_UAT.TB_INVOICE_UNIT
	WHERE ORDER_NO = (SELECT JOB_ODR_NO2 FROM DNP_UAT.TB_SSR 

					 CNTR_NO = #{containerNo}
					 AND BL_NO = #{blNo}
					 AND BOOKING_NO = #{bookingNo}
					 AND VSL_CD = #{vslNm}
					 AND CALL_SEQ = #{voyNo}
					 AND FE = #{fe}
					 AND PTNR_CODE = #{opeCode}			

        GROUP BY JOB_ODR_NO2)

	SELECT 
	    INV_NO AS INVOICE_NO,
	    NET_AMT AS EXCHANGE_FEE,
	    VAT_RATE AS VAT_RATE,
	    AMT AS VAT_AFTER_FEE,
	    CNTR_NO AS CONTAINER_NO,
	    SZTP2 AS SZTP
	FROM DNP_UAT.TB_INVOICE_UNIT
	WHERE ORDER_NO = (SELECT JOB_ODR_NO FROM DNP_UAT.TB_SSR 

					 CNTR_NO = #{containerNo}
					 AND BL_NO = #{blNo}
					 AND BOOKING_NO = #{bookingNo}
					 AND VSL_CD = #{vslNm}
					 AND CALL_SEQ = #{voyNo}
					 AND FE = #{fe}
					 AND PTNR_CODE = #{opeCode}			

        GROUP BY JOB_ODR_NO)
