mapper.update:
update pickup_history
logistic_group_id = #{logisticGroupId}, shipment_id = #{shipmentId}, shipment_detail_id = #{shipmentDetailId}, driver_id = #{driverId}, pickup_assign_id = #{pickupAssignId}, container_no = #{containerNo}, truck_no = #{truckNo}, chassis_no = #{chassisNo}, area = #{area}, bay = #{bay}, block = #{block}, line = #{line}, tier = #{tier}, status = #{status}, receipt_date = #{receiptDate}, gatein_date = #{gateinDate}, gateout_date = #{gateoutDate}, cancel_receipt_date = #{cancelDeceiptDate}, planning_date = #{planningDate}, process_order_id = #{processOrderId}, gate_pass = #{gatePass}, job_order_no = #{jobOrderNo}, job_order_flg = #{jobOrderFlg}, loadable_wgt = #{loadableWgt}, sztp = #{sztp}, distance = #{distance}, update_location_time = #{updateLocationTime}, remark = #{remark}, update_by = #{updateBy}, update_time = sysdate
where id = #{id}

mapper.select:
select count(*) from pickup_history 
where
	shipment_id = #{shipmentId}
	and shipment_detail_id = #{shipmentDetailId}

select p.id, p.logistic_group_id, p.shipment_id, p.shipment_detail_id, p.driver_id, p.pickup_assign_id, p.container_no, p.truck_no, p.chassis_no, p.status, p.gatein_date, d.mobile_number, d.full_name from pickup_history p left join driver_account d on p.driver_id = d.id
p.status != 2 and p.logistic_group_id = #{logisticGroupId}
group by p.id, p.logistic_group_id, p.shipment_id, p.shipment_detail_id, p.driver_id, p.pickup_assign_id, p.container_no, p.truck_no, p.chassis_no, p.status, p.gatein_date, d.mobile_number, d.full_name

select count(*) from pickup_history
where driver_id != #{driverId}
and truck_no = #{truckNo}
and chassis_no = #{chassisNo}
and status != 2

select vsl_nm as vslNm, voy_no as voyNo, sztp as sztp, discharge_port as dischargePort, container_amount as containerAmount from (select sh.vsl_nm, sh.voy_no, sh.sztp, sh.discharge_port, ( select count(*) from pickup_history p2 left join shipment_detail sh2 on p2.shipment_detail_id = sh2.id where sh2.vsl_nm = sh.vsl_nm and sh2.voy_no = sh.voy_no and sh2.sztp = sh.sztp and sh2.discharge_port = sh.discharge_port and p2.shipment_detail_id is not null and p2.status = 0 ) as container_amount from pickup_history p left join shipment_detail sh on p.shipment_detail_id = sh.id
sh.vsl_nm is not null and sh.voy_no is not null and sh.sztp is not null and p.shipment_detail_id is not null and p.status = 0 and sh.vsl_nm = #{vslNm} and sh.voyNo = #{voyNo}
and (sh.bl_no like concat('%', concat(#{params.blBookingNo}, '%')) or sh.booking_no like concat('%', concat(#{params.blBookingNo}, '%')))
) group by vsl_nm, voy_no, sztp, discharge_port, container_amount

select distance as distance, container_no as containerNo, vsl_Nm_Voy_No as vslNmVoyNo, sztp as sztp, discharge_port as dischargePort, truck_no as truckNo, gate_pass as gatePass, update_location_time as updateLocationTime, logistic_name as logisticName from (select p.distance, sh.container_no, concat(sh.vsl_nm, concat(' - ', sh.voy_no)) as vsl_Nm_Voy_No, sh.sztp, sh.discharge_port, p.truck_no, p.gate_pass, p.update_location_time, (select l.group_name from logistic_group l where l.id = p.logistic_group_id FETCH NEXT 1 ROWS ONLY) as logistic_name from pickup_history p left join shipment_detail sh on p.shipment_detail_id = sh.id
sh.vsl_nm is not null and sh.voy_no is not null and sh.sztp is not null and p.shipment_detail_id is not null and p.status = 0
and sh.vsl_nm in
#{vslNm}
and sh.voy_no in
#{voyNo}
and sh.sztp in
#{sztp}
and sh.vsl_nm = #{vslNm} and sh.voyNo = #{voyNo}
and (sh.bl_no like concat('%', concat(#{params.blBookingNo}, '%')) or sh.booking_no like concat('%', concat(#{params.blBookingNo}, '%')))
) order by distance asc

		select count(*) from pickup_history

			 and logistic_group_id = #{logisticGroupId}
			 and shipment_id = #{shipmentId}
			 and shipment_detail_id = #{shipmentDetailId}
			 and driver_id = #{driverId}
			 and pickup_assign_id = #{pickupAssignId}
			 and container_no = #{containerNo}
			 and truck_no = #{truckNo}
			 and chassis_no = #{chassisNo}
			 and area = #{area}
			 and bay = #{bay}
			 and block = #{block}
			 and line = #{line}
			 and tier = #{tier}
			 and status = #{status}
			 and receipt_date = #{receiptDate}
			 and gatein_date = #{gateinDate}
			 and gateout_date = #{gateoutDate}
			 and cancel_receipt_date = #{cancelDeceiptDate}
			 and planning_date = #{planningDate}
            and (gatein_date = #{fromDate} OR gatein_date > #{fromDate})
            and (gatein_date = #{toDate} OR gatein_date < #{toDate})
            and process_order_id = #{processOrderId}

