mapper.select:


     and logistic_account_id = #{logisticAccountId}
     and logistic_group_id = #{logisticGroupId}
     and service_type = #{serviceType}
     and upper(bl_no) like concat('%', #{blNo}, '%')
     and upper(booking_no) like concat('%', #{bookingNo}, '%')
     and ope_code = #{opeCode}
     and tax_code = #{taxCode}
     and group_name like concat(concat('%', #{groupName}), '%')
     and address like concat(concat('%', #{address}), '%')
     and container_amount = #{containerAmount}
     and edo_flg = #{edoFlg}
     and specific_cont_flg = #{specificContFlg}
     and cont_supply_status = #{contSupplyStatus}
     and reference_no = #{referenceNo}
     and status = #{status}
     and remark = #{remark}
    and create_time >= #{fromDate}
    and create_time <= #{toDate}

order by create_time desc

mapper.update:
update shipment
logistic_account_id = #{logisticAccountId}, logistic_group_id = #{logisticGroupId}, service_type = #{serviceType}, bl_no = #{blNo}, booking_no = #{bookingNo}, ope_code = #{opeCode}, tax_code = #{taxCode}, group_name = #{groupName}, address = #{address}, container_amount = #{containerAmount}, edo_flg = #{edoFlg}, specific_cont_flg = #{specificContFlg}, cont_supply_status = #{contSupplyStatus}, and reference_no = #{referenceNo}, status = #{status}, remark = #{remark}, update_by = #{updateBy}, update_time = sysdate
where id = #{id}

mapper.select:
select count(*) from shipment

     and service_type = #{serviceType}
     and bl_no = #{blNo}
     and booking_no = #{bookingNo}

select  id, logistic_account_id, logistic_group_id, service_type, bl_no, booking_no, ope_code, tax_code, group_name, edo_flg, specific_cont_flg, cont_supply_status, reference_no, status, remark, create_by, create_time, update_by, update_time,
        (select count(*) from shipment_detail sh where sh.shipment_id = s.id and sh.process_status = 'Y') as container_amount
from shipment s

    (select count(*) from shipment_detail sh where sh.shipment_id = s.id and sh.process_status = 'Y') > 0
     and container_amount > 0
     and logistic_account_id = #{logisticAccountId}
     and logistic_group_id = #{logisticGroupId}
     and service_type = #{serviceType}
     and bl_no = #{blNo}
     and booking_no = #{bookingNo}
     and ope_code = #{opeCode}
     and tax_code = #{taxCode}
     and group_name like concat(concat('%', #{groupName}), '%')
     and edo_flg = #{edoFlg}
     and specific_cont_flg = #{specificContFlg}
     and cont_supply_status = #{contSupplyStatus}
     and reference_no = #{referenceNo}
     and status = #{status}
     and remark = #{remark}

order by create_time desc
