select id, carrier_id, carrier_code, order_number, bill_of_lading, business_unit, consignee, count(container_number) as container_number, expired_dem, empty_container_depot, det_free_time, secure_code, release_date, vessel, voy_no, do_type, status, process_status, document_status, document_receipt_date, release_status, create_source, remark, create_by, create_time, update_by, update_time 
from equipment_do


     and bill_of_lading like concat('%', #{billOfLading}, '%')  
     and carrier_id = #{carrierId}
     and carrier_code = #{carrierCode}
     and order_number = #{orderNumber}
     and container_number like concat('%', #{containerNumber}, '%')
     and status = #{status}
     and document_status = #{documentStatus}
     and process_status = #{processStatus}
     and release_status = #{releaseStatus}
     and create_source = #{createSource}
     and vessel like concat('%', #{vessel}, '%')
     and voy_no like concat('%', #{voyNo}, '%')
     and expired_dem between #{fromDate} and #{toDate}
     and expired_dem = #{fromDate} OR expired_dem > #{fromDate}
     and expired_dem = #{toDate} OR expired_dem < #{toDate} 

group by bill_of_lading
ORDER BY id DESC

		SELECT 
